<template>
  <div>
    <h1>Name : {{ name }}</h1>
    <h3>Age : {{ age }}</h3>
    <img :src="profile" alt="profile" />
    <h4 v-if="score >= 50" style="color: #0c74dd">Status : Pass</h4>
    <h4 v-else style="color: red">Status : Fail</h4>
    <div class="bouns">
      <button @click="decrement">-</button>
      <p>{{ score }}</p>
      <button @click="increment">+</button>
    </div>
  </div>
</template>
<script>
export default {
  data() {
    return {
      name: "senghak chhun",
      age: 19,
      profile:
        "data:image/jpeg;base64,",
      score: 20,
    };
  },
  methods: {
    increment() {
      this.score += 10;
    },
    decrement() {
      this.score -= 10;
    },
  },
};
</script>

